mapper.select:
SELECT distinct exam_subject
  FROM exam_info
 WHERE exam_type_id = (
 	SELECT exam_type_id
	  FROM exam_type
	 WHERE exam_type_code = #{examTypeCode}
 )
   AND exam_round = #{examRound}
 ORDER BY exam_subject

SELECT COUNT(*)
  FROM exam_info
 WHERE exam_type_id = #{examTypeId}
   AND exam_round = #{examRound}
   AND exam_subject = #{examSubject}
