mapper.select:
SELECT exam_id
  FROM exam_info
 WHERE exam_type_id = (
	SELECT exam_type_id
	  FROM exam_type
	 WHERE exam_type_code = #{examTypeEng}
 )
   AND ISDELETED = 'N'
   AND EXAM_ROUND = #{examRound}
   AND EXAM_SUBJECT = #{examSubject}

SELECT exam_type_id
  FROM exam_type
 WHERE exam_type_code = #{examTypeEng}

SELECT COUNT(*)
  FROM exam_info
 WHERE exam_type_id = #{examTypeId}
   AND exam_round = #{examRound}
   AND exam_subject = #{examSubject}
   AND isdeleted = 'N'

SELECT exam_type_name
  FROM exam_type
 WHERE exam_type_code = #{examType}

SELECT Q.passage_scope, Q.commonpassage
  FROM exam_question Q
  JOIN exam_info I on Q.exam_id = I.exam_id
  JOIN exam_type T on I.exam_type_id = T.exam_type_id
 WHERE T.exam_type_code = #{examType}
   AND I.exam_round = #{examRound}
   AND I.exam_subject = #{examSubject}
   AND Q.passage_scope IS NOT NULL
   AND I.isdeleted = 'N'

SELECT count(question_num)
  FROM exam_question
 WHERE exam_id = #{examId}

SELECT subject_id
  FROM exam_subject
 WHERE exam_subject = #{examSubject}
   AND exam_type_id = #{examTypeId}

SELECT exam_type_id
  FROM exam_type
 WHERE exam_type_code = #{type}

SELECT f.folder_name
  FROM exam_folder f
  JOIN exam_info i ON i.folder_id = f.folder_id
 WHERE i.exam_type_id = #{examTypeId}
   AND i.exam_round = #{examRound}
   AND i.exam_subject = #{examSubject}
   AND i.isdeleted = 'N'

SELECT folder_id
  FROM exam_info
 WHERE exam_id = #{examId}

mapper.update:
UPDATE exam_question
   SET question_text = #{questionText},
	   individualpassage = #{individualPassage},
	   commonpassage = #{commonPassage},
	   passage_scope = #{passageScope}
 WHERE question_id = #{questionId}

UPDATE exam_choice
   SET choice_text = #{choiceText}
 WHERE choice_id = #{choiceId}

mapper.select:
SELECT question_id
  FROM exam_question
 WHERE exam_id = #{examId}
